    target="honeycomb-triggers")
example_query = honeycomb.query(calculations=[honeycomb.QueryCalculationArgs(
    op="AVG",
    column="duration_ms",
)])
example_trigger = honeycomb.Trigger("exampleTrigger",
    query_json=example_query.json,
    dataset=dataset,
    threshold=honeycomb.TriggerThresholdArgs(
        op=">",
        value=1000,
    ),
    recipients=[
        honeycomb.TriggerRecipientArgs(
            type="email",
            target="hello@example.com",
        ),
        honeycomb.TriggerRecipientArgs(
            id=slack.id,
        ),
    ])
