        op="AVG",
        column="duration_ms",
    )],
    filters=[honeycomb.GetQuerySpecificationFilterArgs(
        column="trace.parent_id",
        op="does-not-exist",
    )])
example_query = honeycomb.Query("exampleQuery",
    dataset=dataset,
    query_json=example_get_query_specification.json)
example_trigger = honeycomb.Trigger("exampleTrigger",
